      insertIntoPacks(value).then(function(result){}, function(err) {
        alert(insert + " <<<<<< err: " + JSON.stringify(err));
      });
      insertIntoAffectedPacks(type, value).then(function() {}, function (err) {
        alert(insert + " <<<<<< err: " + JSON.stringify(err));
      })

